template <class T1, class T2, class T3, class T4, class T5, class T6, class T7, class T8, class VoxelFunc>
void ParallelForEachScalar(const irtkGenericImage<T1> *im1, const irtkGenericImage<T2> *im2, const irtkGenericImage<T3> *im3, const irtkGenericImage<T4> *im4, const irtkGenericImage<T5> *im5, irtkGenericImage<T6> *im6, irtkGenericImage<T7> *im7, irtkGenericImage<T8> *im8, VoxelFunc &vf)
{
  irtkOctaryForEachVoxelBody_5Const<T1, T2, T3, T4, T5, T6, T7, T8, VoxelFunc> body(*im1, *im2, *im3, *im4, *im5, *im6, *im7, *im8, vf);
  blocked_range<int> re(0, im8->GetNumberOfVoxels());
  if (VoxelFunc::IsReduction()) { parallel_reduce(re, body); vf.join(body._VoxelFunc); }
  else                            parallel_for   (re, body);
}

